import './App.css';

const Format = () =>
{
  return <h2>{comment.date}</h2>;
}

const Avatar = (props) =>{
  return <img src = {props.avatarUrl} />;
}

const Name = (props) =>{
  return <h1>{props.name}</h1> ;
}

const Description = (props) =>{
  return <h2>{props.text}</h2>
}

const comment = {
  date: new Date(),
  text: 'I hope you enjoy learning React!',
  author: {
    name: 'Hello Kitty',
    avatarUrl: 'https://b.thumbs.redditmedia.com/7BBF5u_aOIeVPYLKLoZjvST_uyhnQNyHuwQ7PrXusHs.png'
  }
};


const App = () => {
  return (
    <div className="App">
      <Avatar avatarUrl = {comment.author.avatarUrl}/>
      <Name name = {comment.author.name} />
      <Description text = {comment.text}/>
      <h2>{formatDate(props.date)}</h2>
    </div>
  );
};



export default App;
