// import {useState} from "react";
import './App.css';



const Avatar = (props) =>{
  return <img src = {props.avatarUrl} alt="" width={200} />;
}

const Name = (props) =>{
  return <h1>{props.name}</h1> ;
}

const Description = (props) =>{
  return <h2>{props.text}</h2>
}

const FormatDate = (props) =>{
  return <h2>{props.date.toLocaleDateString()}</h2>;
}

const comment = {
  date: new Date(),
  text: 'I hope you enjoy learning React!',
  author: {
    name: 'Anouar Chennife',
    avatarUrl: 'https://media.licdn.com/dms/image/C5603AQFXEX3wzA6VFQ/profile-displayphoto-shrink_800_800/0/1621411259616?e=2147483647&v=beta&t=2qicbHWstT4tLmwOgPfIk2xC0gXLiaPlnjhRENLaNg0'
  }
};



const App = () => {
  return (
    <div className="App">
      <Avatar avatarUrl = {comment.author.avatarUrl}/>
      <Name name = {comment.author.name} />
      <Description text = {comment.text}/>
      <FormatDate date = {comment.date}/>      

    </div>
  );
};



export default App;
